shader_source_data.source:
vec4 blend, tmp[4], extra_blend, min_blend, max_blend;
vec3 result;
vec2 ddx, ddy;
int i;

ddx = world_extra_uv_ddx_ddy.xy;
ddy = world_extra_uv_ddx_ddy.zw;

result = material_color.rgb;

blend = textureGrad(effect_sampler_7, world_uv, world_uv_ddx_ddy.xy, world_uv_ddx_ddy.zw);

if (any(greaterThan(blend, vec4(0.0))))
{
	tmp[0] = textureGrad(effect_sampler_0, world_extra_uv, ddx, ddy);
	tmp[1] = textureGrad(effect_sampler_1, world_extra_uv, ddx, ddy);
	tmp[2] = textureGrad(effect_sampler_2, world_extra_uv, ddx, ddy);
	tmp[3] = textureGrad(effect_sampler_3, world_extra_uv, ddx, ddy);

	for (i = 0; i < 4; ++i)
	{
		extra_blend[i] = tmp[i].a;
	}

	min_blend = clamp(extra_blend - blend_sizes[0], 0.0, 1.0);
	max_blend = clamp(extra_blend + blend_sizes[0], 0.0, 1.0);

	blend = smoothstep(min_blend, max_blend, blend);

	for (i = 3; i >= 0; --i)
	{
		result = mix(result, tmp[i].rgb, blend[i]);
	}
}

output_data[0].rgb = result;
output_data[0].a = 1.0;

vec4 blend, tmp[4], extra_blend;
vec3 result;
int i;

result = material_color.rgb;

blend = texture2D(effect_sampler_7, world_uv);

tmp[0] = texture2D(effect_sampler_0, world_extra_uv);
tmp[1] = texture2D(effect_sampler_1, world_extra_uv);
tmp[2] = texture2D(effect_sampler_2, world_extra_uv);
tmp[3] = texture2D(effect_sampler_3, world_extra_uv);

for (i = 0; i < 4; ++i)
{
	extra_blend[i] = tmp[i].a;
}

blend = smoothstep(extra_blend - blend_sizes[0], extra_blend + blend_sizes[0], blend);

for (i = 3; i >= 0; --i)
{
	result = mix(result, tmp[i].rgb, blend[i]);
}

output_data[0].rgb = result;
output_data[0].a = 1.0;
