shader_source_data.source:
mat2x4 transformation;
vec2 fenc;
float f, g;

world_position = morph_position.xyz;
world_position.xy = world_position.xy * 2.0 - 1.0;
world_position = world_position * terrain_vector_scale + position.xyz;

fenc = morph_normal.xy * 3.968871595 - 1.984435798;
f = dot(fenc, fenc);
g = sqrt(1.0f - f / 4.0);
world_normal = vec3(fenc * g, 1.0 - f / 2.0);

world_tangent = world_normal;

transformation = world_transformations[gl_InstanceID];

world_position += 2.0 * cross(transformation[0].xyz, cross(transformation[0].xyz, world_position) + transformation[0].w * world_position);
world_position *= transformation[1].w;
world_position += transformation[1].xyz;
world_normal += 2.0 * cross(transformation[0].xyz, cross(transformation[0].xyz, world_normal) + transformation[0].w * world_normal);
world_tangent += 2.0 * cross(transformation[0].xyz, cross(transformation[0].xyz, world_tangent) + transformationation[0].w * world_tangent);
terrain_texture_coodrinate = position.xy + transformation[1].xy;

mat2x4 transformation;
vec2 fenc;
float f, g;

world_position = morph_position.xyz;
world_position.xy = world_position.xy * 2.0 - 1.0;
world_position = world_position * terrain_vector_scale + position.xyz;

fenc = morph_normal.xy * 3.968871595 - 1.984435798;
f = dot(fenc, fenc);
g = sqrt(1.0f - f / 4.0);
world_normal = vec3(fenc * g, 1.0 - f / 2.0);

world_tangent = world_normal;

transformation = world_transformations[0];

world_position += 2.0 * cross(transformation[0].xyz, cross(transformation[0].xyz, world_position) + transformation[0].w * world_position);
world_position *= transformation[1].w;
world_position += transformation[1].xyz;
world_normal += 2.0 * cross(transformation[0].xyz, cross(transformation[0].xyz, world_normal) + transformation[0].w * world_normal);
world_tangent += 2.0 * cross(transformation[0].xyz, cross(transformation[0].xyz, world_tangent) + transformationation[0].w * world_tangent);
terrain_texture_coodrinate = position.xy + transformation[1].xy;
