shader_source_data.source:
{
	vec4 weight, index, tmp;
	vec3 temp;
	int i, idx;

	tmp = vec4(position.xyz, 1.0);
	index = bone_index;
	weight = bone_weight;

	world_position = vec3(0.0);

	for (i = 0; i < 4; i++)
	{
		idx = int(index.x);
		world_position.x += dot(bones[idx * 3 + 0], tmp) * weight.x;
		world_position.y += dot(bones[idx * 3 + 1], tmp) * weight.x;
		world_position.z += dot(bones[idx * 3 + 2], tmp) * weight.x;

		if (weight.y <= 0.0)
		{
			break;
		}

		index.xyz = index.yzw;
		weight.xyz = weight.yzw;
	}

	world_position += 2.0 * cross(world_transform[0].xyz, cross(world_transform[0].xyz, world_position) + world_transform[0].w * world_position);
	world_position *= world_transform[1].w;
	world_position += world_transform[1].xyz;
}
